
function no_order(a) {
    var b = a.length;
    if (1 == b)
        var c = "000000" + a;
    2 == b && (c = "00000" + a);
    3 == b && (c = "0000" + a);
    4 == b && (c = "000" + a);
    5 == b && (c = "00" + a);
    6 == b && (c = "0" + a);
    7 == b && (c = a);
    0 == b && (c = "0000001");
    return c
}
function table_no(a) {
    var b = a.length;
    if (1 == b)
        var c = "0" + a;
    2 == b && (c = a);
    0 == b && (c = "01");
    return"No. " + c
}
function fin_code(a) {
    var b = a.length;
    return"NUP" + (1 == b ? "000000" + a : 2 == b ? "00000" + a : 3 == b ? "0000" + a : 4 == b ? "000" + a : 5 == b ? "00" + a : 6 == b ? "0" + a : 7 == b ? a : "0000001")
}
function numberToCurrency(a) {
    if ("" != a && null != a) {
//        a = Math.round(a);
        a = a.toString();
        a = a.replace(/[^\d\,]/g, "").split(",");
        for (var b = "", c = 0, d = a[0].length; 0 < d; d--)
            c += 1, b = 1 == c % 3 && 1 != c ? a[0].substr(d - 1, 1) + "." + b : a[0].substr(d - 1, 1) + b;
        1 < a.length && (b = 0 < a[1].length ? b + ("," + a[1]) : b + ",");
        return b
    }
    return""
}
function format_num(a) {
    a.value = numberToCurrency(a.value)
}
function calcPay(a, b) {
    a.value = numberToCurrency(a.value);
    var c = currencyToNumber($("#price_" + b).val() ? $("#price_" + b).val() : "0"), d = currencyToNumber($("#qty_" + b).val() ? $("#qty_" + b).val() : "0");
    $(".total_" + b).html("Rp " + numberToCurrency(c * d));
    $("#total_add_" + b).val(c * d);
    for (var c = $(".row_out").length, d = 0, f = 1; f <= c; f++)
        var e = currencyToNumber($("#total_add_" + f).val() ? $("#total_add_" + f).val() : 0), d = d + e;
    $(".sub_total").val("" + d + "");
    $(".total_sub").val(numberToCurrency("" + d + ""));
    $(".total").val(numberToCurrency("" +
            d + ""));
    e = $("#hide_diskon").val();
    c = currencyToNumber($(".diskon").val() ? $(".diskon").val() : 0);
    d = currencyToNumber($(".total_sub").val() ? $(".total_sub").val() : 0);
    f = currencyToNumber($(".bayar").val() ? $(".bayar").val() : 0);
    "rp" == e ? c > d ? ($(".diskon").val(0), e = d) : (e = d - c, $(".inp_dis").val(c)) : 100 < c ? ($(".diskon").val(0), e = d) : (e = d - c / 100 * d, $(".inp_dis").val(c / 100 * d));
    $(".total").val(numberToCurrency("" + e + ""));
    $(".kembali").val(f ? f < e ? 0 : numberToCurrency("" + (f - e) + "") : 0);
    $(".kekurangan").val(f ? f > e ? 0 : numberToCurrency("" +
            (f - e) + "") : 0)
}
function format_pay(a) {
    a.value = numberToCurrency(a.value);
    var b = $("#hide_diskon").val();
    a = currencyToNumber($(".diskon").val() ? $(".diskon").val() : 0);
    var c = currencyToNumber($(".total_sub").val() ? $(".total_sub").val() : 0), d = currencyToNumber($(".bayar").val() ? $(".bayar").val() : 0);
    "rp" == b ? a > c ? ($(".diskon").val(0), b = c) : (b = c - a, $(".inp_dis").val(a)) : 100 < a ? ($(".diskon").val(0), b = c) : (b = c - a / 100 * c, $(".inp_dis").val(a / 100 * c));
    $(".total").val(numberToCurrency("" + b + ""));
    $(".kembali").val(d ? d < b ? 0 : numberToCurrency("" + (d -
            b) + "") : 0);
    $(".kekurangan").val(d ? d > b ? 0 : numberToCurrency("" + (d - b) + "") : 0)
}
function format_out(a, b) {
    a.value = numberToCurrency(a.value);
    var c = $(".row_out").length;
    calc_out_sub(b);
    calc_out(c)
}
function calc_out_sub(a) {
    var b = currencyToNumber($(".price" + a).val() ? $(".price" + a).val() : 0), c = currencyToNumber($(".qty" + a).val() ? $(".qty" + a).val() : 0);
    $(".total" + a).html("Rp. " + floatToCurrency("" + b * c + "") + "")
}
function calc_out(a) {
    var b = 0;
    for (i = 1; i <= a; i++)
        var c = currencyToNumber($(".price" + i).val() ? $(".price" + i).val() : 0), d = currencyToNumber($(".qty" + i).val() ? $(".qty" + i).val() : 0), b = b + c * d;
    $(".total").val(floatToCurrency("" + b + ""))
}
function number(a) {
    a.value = parseNumber(a.value)
}
function parseNumber(a) {
    if ("" != a && null != a) {
        a = a.toString();
        a = a.replace(/[^\d\,]/g, "").split(",");
        for (var b = "", c = a[0].length; 0 < c; c--)
            b = a[0].substr(c - 1, 1) + b;
        1 < a.length && (b = 0 < a[1].length ? b + ("," + a[1]) : b + ",");
        return b
    }
    return""
}
function komaKeTitik(a) {
    a = a.toString();
    var b = "";
    null != a && (b = a.replace(/\,/g, "."));
    return b
}
function currencyToNumber(a) {
    var b = a ? a.toString() : "", c = "";
    if (null != a || "" != a)
        c = b.replace(/\.+/g, "");
    return parseFloat(komaKeTitik(c))
}
function numberToCurrency2(a) {
    if (null == a || isNaN(a))
        return"";
    a = parseInt(a).toString();
//    a = Math.round(a);
    for (var b = "", c = 0, d = a.length; 0 < d; d--)
        c += 1, b = 1 == c % 3 && 1 != c ? a.substr(d - 1, 1) + "." + b : a.substr(d - 1, 1) + b;
    return b
}
function floatToCurrency(a) {
    if ("" != a && null != a) {
        a = a.toString();
        var b = a.replace(/[^\d\.]/g, "");
        a = a.split(".");
        return 1 < a.length ? (b = a[1].substr(0, 2), numberToCurrency2(a[0]) + "," + b) : numberToCurrency2(b)
    }
    return""
}