$(".logout").click(function() {
    $(this).each(function() {
        bootbox.confirm("<i class='fa fa-warning text-warning mg-r-sm' style='font-size: 24px'></i><i>Do you want to exit the system</i> ?", function(a) {
            1 == a && (window.location.href = baseUrl + "logout")
        })
    })
});
$(".ngFolarium").click(function() {
    $(".modal-title").html('<div class="loader mg-t"><i class="fa fa-spin fa-refresh mg-r-md"></i>Loading data. Please wait...');
    $("#modal-content").html("");
    $("#modal-content").load(baseUrl + "dashboard/about")
});
function loadModul(url){
    $(".modal-title").html('<div class="loader mg-t"><i class="fa fa-spin fa-refresh mg-r-md"></i>Loading data. Please wait...');
    $("#modal-contents").html("");
    $("#modal-contents").load(baseUrl + url);
}
